import React,{useState,useEffect} from 'react'
import axios from "axios"
import { useNavigate,useParams } from 'react-router-dom';

function UpdateNote() {
    const navigate = useNavigate();
  const[tittle ,setTittle]=useState("");
  const[description ,setDes]=useState("");
  const[price ,setPrice]=useState("");
  const[available ,setAvailable]=useState("");
  const[image ,setImage]=useState("");
  const params = useParams();

  useEffect(() => {
    getProductDetails();
  }, [])

  const getProductDetails = async()=>{
    console.log(params.id)
    let result = await axios.get(`http://localhost:5000/updatesaree/${params.id}`)
    console.log(result.data);
    if(result){
        setType(result.data.type);
        setCompany(result.data.company);
        setPrice(result.data.price);
        setAvailable(result.data.available); 
        setImage(result.data.image);
    }
  }
  return (
    <div>
    <h1 style={{ textAlign: "center" }}>Update your Note</h1>
    <div>
      <form style={{textAlign:"center"}}>
        <label style={{fontSize:"28px"}}>Tittle:</label>
         <br></br>
          <input style={{width:"25%",height:"4vh",marginBottom:"2%",fontSize:"20px"}}type="text" 
          name="name"  required={true}/>
        <br></br>
        <label style={{fontSize:"28px"}}>
          Description:
          </label>
          <br></br>
          <input style={{width:"25%",height:"4vh",marginBottom:"2%",fontSize:"20px"}} type="text"
           name="description" required={true}/>
          <br></br>
          <div style={{background: "cornflowerblue", marginLeft: "46%",marginRight: "45%"}}>
            <p style={{fontSize:"30px",color:"white"}}>Update</p>
          </div>
      </form>
    </div>
  </div>
  )
}

export default UpdateNote
